import React, { useEffect, useState, useCallback } from 'react';
import NewsItem from './NewsItem';
import Spinner from './Spinner';
import PropTypes from 'prop-types';
import InfiniteScroll from 'react-infinite-scroll-component';

const News = (props) => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [totalResults, setTotalResults] = useState(0);
  const [dataLength, setDataLength] = useState(0); // Initialize dataLength with 0

  // ... Rest of your code ...

  const updateNews = useCallback(async () => {
    // ... Your existing code ...

    setArticles(parsedData.articles);
    setTotalResults(parsedData.totalResults);
    setLoading(false);
    props.setProgress(100);

    // Update dataLength when articles are populated
    setDataLength(parsedData.articles.length);
  }, [page, props]);

  useEffect(() => {
    document.title = `${capitalizeFirstLetter(props.category)} - NewsPanda`;
    updateNews();
  }, [props.category, updateNews]);

  // ... Rest of your code ...

  return (
    <>
      <h1
        className={`text-center text-${props.color}`}
        style={{ margin: '35px 0px', marginTop: '90px' }}
      >
        Top {capitalizeFirstLetter(props.category)} Headlines
      </h1>
      {loading && <Spinner />}
      <InfiniteScroll
        dataLength={dataLength} // Use dataLength here
        next={fetchMoreData}
        hasMore={articles.length !== totalResults}
        loader={<Spinner />}
      >
        <div className="container">
          <div className="row">
            {articles.map((element) => {
              return (
                <div className="col-md-4" key={element.url}>
                  <NewsItem
                    color={props.color}
                    bgcolor={props.bgdrill}
                    title={element.title ? element.title : ''}
                    description={element.description ? element.description : ''}
                    imageUrl={element.urlToImage}
                    newsUrl={element.url}
                    author={element.author}
                    date={element.publishedAt}
                    source={element.source.name}
                  />
                </div>
              );
            })}
          </div>
        </div>
      </InfiniteScroll>
    </>
  );
};



News.defaultProps = {
    country: 'in',
    pageSize: 8,
    category: 'general',
}

News.propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string,
}

export default News
